import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';

export default function App() {
  const [inputPhrase, setInputPhrase] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setResults([]);

    try {
      const response = await axios.post('http://localhost:5000/api/query', {
        phrase: inputPhrase
      });
      setResults(response.data.results);
    } catch (err) {
      setError(err.response?.data?.error || 'Something went wrong');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container my-5">
      <h1 className="mb-4">SIMINT</h1>

      <form onSubmit={handleSubmit} className="mb-4">
        <div className="mb-3">
          <label htmlFor="phraseInput" className="form-label">Enter a natural language phrase</label>
          <textarea
            id="phraseInput"
            className="form-control"
            rows={3}
            value={inputPhrase}
            onChange={(e) => setInputPhrase(e.target.value)}
            required
          ></textarea>
        </div>
        <button
          type="submit"
          className="btn btn-primary"
          disabled={loading}
        >
          {loading ? 'Processing...' : 'Submit'}
        </button>
      </form>

      {error && <div className="alert alert-danger">Error: {error}</div>}

      {results.length > 0 && (
        <div>
          <h5>Results</h5>
          {results.map((res, index) => (
            <div key={index} className="card mb-3">
              <div className="card-body">
                {Object.entries(res).map(([key, value]) => (
                  <div key={key} className="mb-2">
                    <strong>{key}</strong>
                    <ul className="list-group list-group-flush ms-3">
                      {value.properties
                        ? Object.entries(value.properties).map(([propKey, propVal]) => (
                            <li key={propKey} className="list-group-item px-0">
                              {propKey}: {typeof propVal === 'object' && propVal.low !== undefined ? propVal.low : propVal}
                            </li>
                          ))
                        : <li className="list-group-item px-0">{JSON.stringify(value)}</li>
                      }
                    </ul>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
